import React, { useEffect, useState } from 'react';
import vtk from 'vtk.js';

function App() {
  const [renderWindow, setRenderWindow] = useState(null);
  const [connection, setConnection] = useState(null);
  const [viewId, setViewId] = useState(null);

  const setupConnection = async () => {
    const connection = vtk.Rendering.Misc.vtkWSLinkClient.newInstance();
    await connection.connect('ws://your-server-ip:8080/ws');
    setConnection(connection);

    const viewId = await connection.getRemote().createView();
    setViewId(viewId);
  };

  const renderVolume = async () => {
    if (connection && viewId) {
      const renderWindow = await connection.getRemote().getRenderWindow(viewId);
      setRenderWindow(renderWindow);

      const render = async () => {
        if (renderWindow) {
          renderWindow.render();
          requestAnimationFrame(render);
        }
      };

      requestAnimationFrame(render);

      // Trigger server-side volume rendering
      await connection.getRemote().call('initialize', viewId);
    }
  };

  useEffect(() => {
    setupConnection();

    return () => {
      if (connection) {
        connection.disconnect();
      }
    };
  }, []);

  return (
    <div>
      <button onClick={renderVolume}>Render Volume</button>
      {renderWindow && (
        <vtk.View
          id={`vtk-view-${viewId}`}
          renderWindow={renderWindow}
          height={600}
          width={800}
        />
      )}
    </div>
  );
}

export default App;
