import React,{Component}  from 'react';
import vtkFullScreenRenderWindow from '@kitware/vtk.js/Rendering/Misc/FullScreenRenderWindow';
import vtkWSLinkClient from '@kitware/vtk.js/IO/Core/WSLinkClient';
import vtkVolumeController from '@kitware/vtk.js/Interaction/UI/VolumeController';
import vtkInteractorStyleManipulator from '@kitware/vtk.js/Interaction/Style/InteractorStyleManipulator';

class VTKClient extends Component {
    constructor(props) {
      super(props);
      this.containerRef = React.createRef();
    }

    componentDidMount() {
      // Create VTK.js full-screen renderer
      const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
        container: this.containerRef.current,
      });

      // Initialize WebSocket connection to VTK server
      const wsClient = vtkWSLinkClient.newInstance();
      wsClient.connect('ws://localhost:7500/ws');

      // Create volume controller
      const volumeController = vtkVolumeController.newInstance({
        renderWindow: fullScreenRenderer.getRenderWindow(),
        wsLink: wsClient.getConnection(),
        useShadow: true,
      });

      // Set the default interactor style for the render window
      fullScreenRenderer.getRenderWindow().getInteractor().setInteractorStyle(vtkInteractorStyleManipulator.newInstance());
    }

    render() {
      return <div ref={this.containerRef} className="vtk-container" />;
    }
  }

  export default VTKClient;
