import React, { useRef, useEffect } from 'react';

const VTKClient = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const gl = canvas.getContext('webgl');

    const ws = new WebSocket('ws://localhost:7500');

    ws.binaryType = 'arraybuffer';

    ws.onmessage = event => {
      const data = new Uint8Array(event.data); // Assuming you're receiving Uint8Array data
      const texture = gl.createTexture();

      gl.bindTexture(gl.TEXTURE_2D, texture);
      gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, gl.RGBA, gl.UNSIGNED_BYTE, data);

      // Setup your shaders and other WebGL configurations here
      // Draw the texture on the canvas
    };

    return () => {
      ws.close();
    };
  }, []);

  return <canvas ref={canvasRef} width={800} height={600} />;
};

export default VTKClient;
